async fn example_basic_post_crm_v_3_objects_contacts_gdpr_delete_purge() -> anyhow::Result<()> {
    let client = hubspot_contacts::Client::new_from_env();
    client
        .basic()
        .post_crm_v_3_objects_contacts_gdpr_delete_purge(&hubspot_contacts::types::PublicGdprDeleteInput {
            id_property: Some("some-string".to_string()),
            object_id: "some-string".to_string(),
